select * from (select RANK() over(ORDER BY score+assist_number DESC) AS TED_RANK, player_id, player_name, team_name, score, assist_number,score+assist_number AS tedLinslayAward from (select RANKS,player_id,player_name,team_name,score,assist_number from(select RANK() over(ORDER BY score DESC) AS Ranks, player_id, player_name, team_name, score, assist_number from (select * from ((select * from Hockey_Team@serverPc natural join Hockey_Player@serverPc) UNION (select * from hockey_team natural join hockey_player)) natural join (select * from Hockey_Assist@serverPc UNION select * from hockey_assist))) where Ranks<=5)) where TED_Rank=1;